article.dataset.like; // "3"

function clicked() {
    var xhr = new XMLHttpRequest();
    xhr.open("GET", "https://api.countapi.xyz/hit/www.wendycode.com/awesomeclick");
    xhr.responseType = "json";
    xhr.onload = function() {
        like.setAttribute("data-klik", '${this.response.value}');
    }
    xhr.send();
}
