/**/
function StorageManipulation(e, t) {
  t = t || "localStorage";
  var i = window, n = this;

  if (!(t in i)) return !1;

  this.name = e;
  this.type = t;

  // Mendapatkan semua item
  this.get = function() {
    var e = n.type, t = i[e].getItem(n.name);
    let items = t ? JSON.parse(t) : [];
    // Urutkan berdasarkan timestamp dari terbaru ke yang lama
    items.sort((a, b) => b.timestamp - a.timestamp);
    return items;
  };

  // Mencari item berdasarkan id
  this.find = function(e, t) {
    for (var i = n.get(), r = { items: i }, a = 0; a < i.length; a++)
      if (i[a][e] == t) {
        r.item = i[a], r.index = a;
        break;
      }
    return r;
  };

  // Menghapus item berdasarkan id
  this.delete = function(e, t) {
    var r = n.find(e, t), a = r.items, s = r.index;
    if (s >= 0) {
      a.splice(s, 1);
      0 != a.length ? i[n.type].setItem(n.name, JSON.stringify(a)) : i[n.type].removeItem(n.name);
    }
    return a;
  };

  // Menambahkan item baru
  this.add = function(e) {
    var t = n.get();
    if ("object" != typeof e || Array.isArray(e)) return "please insert item object";
    var r = "id" in e ? "id" : Object.keys(e)[0];
    // Hapus item lama jika sudah ada dan tambahkan timestamp untuk yang baru
    t = n.delete(r, e[r]);
    e.timestamp = Date.now();  // Menambahkan timestamp saat bookmark ditambahkan
    t.push(e);
    try {
      i[n.type].setItem(n.name, JSON.stringify(t));
    } catch (e) {
      return console.log(e), e;
    }
  };

  // Fungsi clear untuk menghapus semua item
  this.clear = function() {
    i[n.type].removeItem(n.name);
  };
}


(function () {
  let buttonBookmark = document.querySelectorAll('.addBookmark'),
    hasil = document.getElementById('hasil'),
    clearButton = document.getElementById('clear-bookmark'), // Pastikan tombol clear-bookmark ada di sini
    book = new StorageManipulation('book');

  // Fungsi untuk menampilkan bookmark yang tersimpan
  function generateBookmark() {
    let items = book.get(),
      html = '';

    items.length > 0 && items.forEach(data => {
      html += '<li><p>' + data.title + '</p>';
      html += '<p>' + data.label + '</p>';
      // Tambahkan anchor link pada img
      html += '<a href="' + data.url + '" target="_blank">';
      html += '<img src="' + data.img + '" alt="Bookmark Image">';
      html += '</a>';
      // Tambahkan tombol delete untuk individual bookmark
      html += '<button class="del-bookmark" data-id="' + data.id + '">Delete</button>';
      html += '</li>';
    });

    hasil && (hasil.innerHTML = html);

    // Pasang event listener untuk tombol delete individual
    let deleteButtons = document.querySelectorAll('.del-bookmark');
    deleteButtons.forEach(button => {
      button.addEventListener('click', () => {
        let id = button.dataset.id;
        book.delete('id', id);  // Hapus bookmark berdasarkan id
        updateBookmarkButtons(); // Update status tombol bookmark
        generateBookmark();     // Update daftar bookmark
      });
    });
  }

  // Fungsi untuk meng-update status tombol bookmark berdasarkan data yang ada di storage
  function updateBookmarkButtons() {
    buttonBookmark.length > 0 && buttonBookmark.forEach(button => {
      let id = button.dataset.id,
        checkItem = book.find('id', id);

      if (checkItem.item) {
        button.innerText = 'Bookmarked';
        button.classList.add('active');
      } else {
        button.innerText = 'Bookmark';
        button.classList.remove('active');
      }
    });
  }

  // Menangani event klik pada tombol bookmark
  buttonBookmark.length > 0 && buttonBookmark.forEach(button => {
    let title = button.dataset.title,
      id = button.dataset.id,
      label = button.dataset.label,
      img = button.dataset.img, // Ambil nilai data-img dari button
      url = button.dataset.url, // Ambil nilai data-url dari button
      checkItem = book.find('id', id);

    // Jika sudah di-bookmark, ubah tombolnya
    if (checkItem.item) {
      button.innerText = 'Bookmarked';
      button.classList.add('active');
    }

    // Event listener untuk menambah atau menghapus bookmark
    button.addEventListener('click', () => {
      let checkClass = button.classList.contains('active');

      if (checkClass) {
        book.delete('id', id);
        button.innerText = 'Bookmark';
        button.classList.remove('active');
      } else {
        book.add({
          'id': id,
          'title': title,
          'label': label,
          'img': img, // Tambahkan data-img ke dalam bookmark
          'url': url  // Tambahkan data-url ke dalam bookmark
        });
        button.innerText = 'Bookmarked';
        button.classList.add('active');
      }

      generateBookmark(); // Update daftar bookmark
    });
  });

  // Event listener untuk tombol Clear All Bookmark
  if (clearButton) {  // Pastikan tombol clear-button ada sebelum dipasang event listener
    clearButton.addEventListener('click', () => {
      book.clear(); // Hapus semua bookmark
      updateBookmarkButtons(); // Update status tombol bookmark
      generateBookmark(); // Update daftar bookmark
    });
  }

  // Update status tombol saat halaman dimuat
  generateBookmark();
  updateBookmarkButtons();
})();

